import re

def split_text_into_sentences(text):
    """
    å°†æ–‡æœ¬åˆ’åˆ†ä¸ºå¥å­ï¼Œæ”¯æŒä¸­è‹±æ–‡æ··åˆå’Œemojiè¡¨æƒ…ã€‚
    
    å‚æ•°:
        text (str): è¾“å…¥æ–‡æœ¬
        
    è¿”å›:
        tuple: (sentences_list, indices_list)
            - sentences_list: åˆ’åˆ†å¥½çš„å¥å­åˆ—è¡¨
            - indices_list: æ¯ä¸ªå¥å­ç»“æŸä½ç½®åœ¨åŸæ–‡ä¸­çš„ç´¢å¼•åˆ—è¡¨
    """
    # ä¸­æ–‡å¥æœ«æ ‡ç‚¹: ã€‚ï¼ï¼Ÿâ€¦
    # è‹±æ–‡å¥æœ«æ ‡ç‚¹: .!?
    # è€ƒè™‘çœç•¥å·: ... æˆ– ã€‚ã€‚ã€‚
    # è€ƒè™‘å¼•å·åçš„å¥æœ«æ ‡ç‚¹
    
    # åŒ¹é…å¥å­ç»“æŸçš„æ¨¡å¼
    sentence_end_pattern = r'(?<=[ã€‚ï¼ï¼Ÿ\.\!\?])[^ã€‚ï¼ï¼Ÿ\.\!\?]*(?=[ã€‚ï¼ï¼Ÿ\.\!\?])|(?<=[\.\!\?])\s+'
    
    # ç‰¹æ®Šå¤„ç†çœç•¥å·
    text = re.sub(r'\.{3,}', '...', text)  # å°†è¿ç»­3ä¸ªä»¥ä¸Šçš„ç‚¹æ›¿æ¢ä¸º3ä¸ªç‚¹
    text = re.sub(r'ã€‚{3,}', 'ã€‚ã€‚ã€‚', text)  # å°†è¿ç»­3ä¸ªä»¥ä¸Šçš„ä¸­æ–‡ç‚¹æ›¿æ¢ä¸º3ä¸ªç‚¹
    
    sentences = []
    indices = []
    
    if not text:
        return [], []
    
    # å…ˆé€šè¿‡æ­£åˆ™è¡¨è¾¾å¼æ‰¾åˆ°æ‰€æœ‰å¯èƒ½çš„å¥å­ç»“æŸä½ç½®
    potential_ends = []
    
    # æŸ¥æ‰¾æ‰€æœ‰å¥æœ«æ ‡ç‚¹
    for match in re.finditer(r'[ã€‚ï¼ï¼Ÿ\.\!\?]+[\"\'"\'"\)\)\>\>]?', text):
        end_pos = match.end()
        potential_ends.append(end_pos)
    
    # å¤„ç†æ²¡æœ‰å¥æœ«æ ‡ç‚¹çš„æƒ…å†µ
    if not potential_ends:
        sentences.append(text)
        indices.append(len(text) - 1)
        return sentences, indices
    
    # æ ¹æ®å¥æœ«æ ‡ç‚¹åˆ’åˆ†å¥å­
    start_pos = 0
    for end_pos in potential_ends:
        # é¿å…ç©ºå¥å­
        if end_pos > start_pos:
            sentence = text[start_pos:end_pos].strip()
            if sentence:
                sentences.append(sentence)
                indices.append(end_pos - 1)  # å­˜å‚¨å¥å­ç»“æŸä½ç½®çš„ç´¢å¼•
            start_pos = end_pos
    
    # å¤„ç†æœ€åä¸€éƒ¨åˆ†
    if start_pos < len(text):
        last_part = text[start_pos:].strip()
        if last_part:
            sentences.append(last_part)
            indices.append(len(text) - 1)
    
    return sentences, indices


